public class R5 {

    /**
     * Finding a Motif in DNA
     * http://rosalind.info/problems/subs/
     */
    public static void start() {

        String text = "";
        String motif = "TAGAACTTA";

        DNA dna = new DNA(text);

        Integer[] indexes = dna.getMotifIndeces(motif);

        for (Integer i : indexes) {

            Dump.print(i+1); Dump.print(" ");

        }

        Dump.ln();

        Dump.println(dna.getMotifIndeces(motif));

    }

}
