public class R1 {

    /**
     * http://rosalind.info/problems/dna/
     */
    public static void start() {

        String text = "";

        int a = 0;
        int c = 0;
        int g = 0;
        int t = 0;

        for (Character ch : text.toCharArray()) {

            if (ch == 'A') a++;
            if (ch == 'C') c++;
            if (ch == 'G') g++;
            if (ch == 'T') t++;

        }

        Dump.println(a + " " + c + " " + g + " " + t);


    }

}
